package com.mepeng.cn.pen.common.util.pinyin4j.format.exception;

/**
 * An exception class indicates the wrong combination of pinyin output formats
 * 
 * @author Li Min (xmlerlimin@gmail.com)
 * 
 */
public class ChinesePinyinOutputFormatCombinationException extends Exception
{
    /**
     * Constructor
     * 
     * @param message
     *            the exception message
     */
    public ChinesePinyinOutputFormatCombinationException(String message)
    {
        super(message);
    }

    /**
     * Automatically generated ID
     */
    private static final long serialVersionUID = -8500822088036526862L;
}
